import React from "react";
import axios from "axios";

class Zipcodeinput extends React.Component {

  static async getInitialProps () {
    var promise = axios.get('http://localhost:4000/speakers').
    then(response => {
      return {
        hasErrored: false,
        speakerData: response.data
      };
    })
    .catch(error => {
      return {
        hasErrored: true,
        message: error.message
      }
    });
    return promise;
  }

  constructor(props) {
    super(props);
    this.state = {
      hasErrored: props.hasErrored,
      message: props.message,
      speakerData: props.speakerData
    }
  }


  componentDidMount() { }

   componentWillUnmount() { }


  render() {
    return (

      <div>
      <ul>
        {this.state.speakerData.map((speaker) =>
        <li key={speaker.id}>
          {speaker.firstName} {speaker.lastName}
        </li>
        )
        }
      </ul>


    <form onSubmit={this.handleSubmit}>
      <div className="row">
        <div className=" col-12 col-sm-12  dwi-zip-input">
          <label>
              Zip Code:<br/>
            <input
              type="text"
              name="zipcode"
              className="dwi-zip-code"
              onChange={this.handleChange}
            />
          </label>
          <div className="dwi-city-div">City-alongcity,   State-areallylongstate</div>
          </div>
          <div className=" col-12 col-sm-12   dwi-zip-input  dwi-zip-btn">
          <label  className="dwi-btn-label">
            <button type="submit" className="dwi-zip-button btn btn-primary btn-sm">   Add </button>
          </label>
          </div>
      </div>
    </form>
    </div>
    );
  }
}


export default Zipcodeinput;
